import React from "react";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { materialDark } from "react-syntax-highlighter/dist/esm/styles/prism";

import s from "./Lessons.module.css";
import sprite from "../../img/icomoon/sprite.svg";
import Scrollspy from "react-scrollspy";
import callBack01 from "../../img/callBack01.jpeg";
import declarative from "../../img/declarative.jpeg";
const CallBack = () => {
  const codeStyle = materialDark;
  return (
    <div className={s.lessonsBox}>
      <div className={s.breadNav}>
        <Scrollspy
          items={["branching", "section-2", "section-3"]}
          currentClassName="is-current"
        >
          <div className={s.breadNavList}>
            <li>
              <a href="#callBack">-</a>
            </li>
            <li>
              <a href="#forEach"> forEach</a>
            </li>
            <li>
              <a href="#arroeFn"> </a>
            </li>
            <li>
              <a href="#codeType"> </a>
            </li>
          </div>
        </Scrollspy>
      </div>
      <div className={s.article}>
        <h1 id="callBack">-</h1>
        <p>
              ,    -  
             ('  ), ,  
                   .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function greet(name) {
           return \`   $\{name}.\`;
           }

           //   greet     
           console.log(greet("")); //   .

           //   greet     
           console.log(greet); //  greet() { return \`  $\{name}.\`; }
                `}
        </SyntaxHighlighter>
        <p>
               {" "}
          <span className={s.Span}>greet</span>      
             .    {" "}
          <strong>  </strong> ,    
          (  ),     .  ,

          .
        </p>
        <p>
          <strong>   (callback, )</strong> - 
          ,        ,  ,
            .
        </p>
        <p>
          <strong>    (higher order function)</strong> -
          ,          
          .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          // -
            function greet(name) {
            console.log(\`   $\{name}.\`);
            }

            //   
            function registerGuest(name, callback) {
            console.log(\`  $\{name}.\`);
            callback(name);
            }

            registerGuest("", greet);
                `}
        </SyntaxHighlighter>
        <p>
               <span className={s.Span}>greet</span>{" "}
           ,      {" "}
          <span className={s.Span}>callback</span>    {" "}
          <span className={s.Span}>registerGuest</span>   . '
              ,  ',  
           .
        </p>
        <h3> </h3>
        <p>
           -      , 
                  . 

          .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function registerGuest(name, callback) {
            console.log(\`  $\{name}.\`);
            callback(name);
            }

            //    greet  
            registerGuest("", function greet(name) {
            console.log(\`  $\{name}.\`);
            });

            //    notify  
            registerGuest("", function notify(name) {
            console.log(\`() $\{name},      30 .\`);
            });
                `}
        </SyntaxHighlighter>
        <h3> </h3>
        <p>
               . , ,
                 .  
           ,   ,  '
             .   
            ,      
             .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function processCall(recipient) {
            //    
            const isRecipientAvailable = Math.random() > 0.5;

            if (!isRecipientAvailable) {
                console.log(\` $\{recipient} ,  .\`);
                //   
            } else {
                console.log(\`  $\{recipient}, ...\`);
             //   
            }
            }

            processCall("");
                `}
        </SyntaxHighlighter>
        <p>
          {" "}
              ,  {" "}
          <span className={s.Span}>processCall</span>    
          '       
          .        
          ?      ,    

            .      
             .    , 

          <span className={s.Span}>onAvailable</span> {" "}
          <span className={s.Span}>onNotAvailable</span>,    
          .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function processCall(recipient, onAvailable, onNotAvailable) {
           //    
           const isRecipientAvailable = Math.random() > 0.5;

           if (!isRecipientAvailable) {
               onNotAvailable(recipient);
               return;
           }

           onAvailable(recipient);
           }

           function takeCall(name) {
           console.log(\`  $\{name}, ...\`);
           //   
           }

           function activateAnsweringMachine(name) {
            console.log(\` $\{name} ,  .\`);
           //   
           }

           function leaveHoloMessage(name) {
           console.log(\` $\{name} ,  .\`);
           //   
           }

           processCall("", takeCall, activateAnsweringMachine);
           processCall("", takeCall, leaveHoloMessage);
                `}
        </SyntaxHighlighter>
        <p>
                 , 
             ,    
          .      -     
          .
        </p>
        <h3> </h3>
        <p>
              .   
             () .    
           .
        </p>
        ,       .  
           <span className={s.Span}>for</span>.
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
            for (let i = 0; i < 10; i += 1) {
                console.log(i);
            } 
                `}
        </SyntaxHighlighter>
        <p>
              "  N "  ? - ,
           ,  {" "}
          <span className={s.Span}>console.log()</span> N .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function repeatLog(n) {
            for (let i = 0; i < n; i += 1) {
             console.log(i);
            }
            }

          repeatLog(5);
                `}
        </SyntaxHighlighter>
        <p>
                ,   ? 
              ,   -  ,  
             .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
         function printValue(value) {
           console.log(value);
         }

         function prettyPrint(value) {
            console.log("Logging value: ", value);
         }

         function repeat(n, action) {
           for (let i = 0; i < n; i += 1) {
            action(i);
         }
         }

         //  printValue  callback-
         repeat(3, printValue);
         // 0
         // 1
         // 2

         //  prettyPrint  callback-
         repeat(3, prettyPrint);
         // Logging value: 0
         // Logging value: 1
         // Logging value: 2
                `}
        </SyntaxHighlighter>
        <h1 id="forEach"> forEach</h1>
        <p>
            ,     {" "}
          <span className={s.Span}>for</span> {" "}
          <span className={s.Span}>for...of</span>     .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          .forEach(function callback(element, index, array) {
            //  -
          });
                `}
        </SyntaxHighlighter>
        <ul>
          <li>  .</li>
          <li> -    .</li>
          <li>  .</li>
        </ul>
        <p>
           -    {" "}
          <span className={s.Span}>element</span>,  {" "}
          <span className={s.Span}>index</span>   
          <span className={s.Span}>array</span>.    
          ,  ,   ,   
            .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const numbers = [5, 10, 15, 20, 25];

           //  for
           for (let i = 0; i < numbers.length; i += 1) {
           console.log(\` $\{i},  $\{numbers[i]}\`);
           }

           //  forEach
           numbers.forEach(function (number, index) {
           console.log(\` $\{index},  $\{number}\`);
           });
                `}
        </SyntaxHighlighter>
        <p>
           ,    {" "}
          <span className={s.Span}>for</span> 
          <span className={s.Span}>for...of</span>    
              .   {" "}
          <span className={s.Span}>forEach</span>  ,   
           .
        </p>
        <h1 id="arroeFn"> </h1>
        <p>
             ,   , 
            ,       
            .
        </p>
        <p>
             {" "}
          <span className={s.Span}> </span>,    
          ,     .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          //   
            function classicAdd(a, b, c) {
            return a + b + c;
            }

            //  ,   
            const arrowAdd = (a, b, c) => {
            return a + b + c;
            };
                `}
        </SyntaxHighlighter>
        <p>
            <span className={s.Span}>function</span> 
          ,     , 
             <span className={s.Span}>=></span>   .
        </p>
        <p>
            ,       
          ,   <span className={s.Span}>=</span>  {" "}
          <span className={s.Span}>=></span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const add = (a, b, c) => {
            return a + b + c;
          };
                `}
        </SyntaxHighlighter>
        <p>  ,       .</p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const add = a => {
            return a + 5;
          };
                `}
        </SyntaxHighlighter>
        <p>
            , '     .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const greet = () => {
            console.log("!");
          };  
                `}
        </SyntaxHighlighter>
        <h3> </h3>
        <p>
               <span className={s.Span}>=></span>{" "}
            .     :     
          .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
           const add = (a, b, c) => {
             console.log(a, b, c);
             return a + b + c;
           }; 
                `}
        </SyntaxHighlighter>
        <p>
                   ,
             <span className={s.Span}>return</span>. 
             (explicit return).  
             ,      
            ,   .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const add = (a, b, c) => a + b + c;
                `}
        </SyntaxHighlighter>
        <p>
             ,    ,  
           <span className={s.Span}>=></span>.   
           (implicit return).    
             <span className={s.Span}>a</span>,{" "}
          <span className={s.Span}>b</span>  <span className={s.Span}>c</span>.
        </p>
        <p>

             ,  ,     

             .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          // 
          function classicAdd(a, b, c) {
          return a + b + c;
          }

          // 
          const arrowAdd = (a, b, c) => a + b + c;
                `}
        </SyntaxHighlighter>
        <h3>
           <span className={s.Span}>arguments</span>
        </h3>
        <p>
               {" "}
          <span className={s.Span}>arguments</span>,    .
                ,  {" "}
          <span className={s.Span}>rest</span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const add = (...args) => {
          console.log(args);
          };

          add(1, 2, 3); // [1, 2, 3]
                `}
        </SyntaxHighlighter>
        <h3>   </h3>
        <p>

          ,  ,     ,
               .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const numbers = [5, 10, 15, 20, 25];

           //  
           numbers.forEach(function (number, index) {
           console.log(\` $\{index},  $\{number}\`);
           });

           //   
           numbers.forEach((number, index) => {
           console.log(\` $\{index},  $\{number}\`);
           });
                `}
        </SyntaxHighlighter>
        <p>
           -      
            .   ,    
                 .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const numbers = [5, 10, 15, 20, 25];

          const logMessage = (number, index) => {
              console.log(\` $\{index},  $\{number}\`);
          };

          numbers.forEach(logMessage);
                `}
        </SyntaxHighlighter>
        <h1 id="codeType"> </h1>
        <p>
                 ,
             .  ,   .
        </p>
        <p>
          <strong>  </strong>   ,  
                . ,
           <span className={s.Span}>for</span>,    
              .
        </p>
        <p>
                .   
                .
        </p>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <img src={callBack01} alt="" />
        </div>
        <h3> </h3>
        <p>
           ,     ,     .
                .
        </p>
        <p>
             HTML-,  ,    
          ,  ,     .  

          HTML-     .
        </p>
        <p>
               .   
             ,     
          .
        </p>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <img src={declarative} alt="" />
        </div>
        <p>
                 . 
          ,   ,    . 
          ,   ,   ,  
                 .
        </p>
        <h3> vs </h3>
        <p>

          .         
          .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          //  
            const numbers = [1, 2, 3, 4, 5];
            const filteredNumbers = [];

            for (let i = 0; i < numbers.length; i += 1) {
               if (numbers[i] > 3) {
                  filteredNumbers.push(numbers[i]);
               }
            }

            console.log(filteredNumbers); // [4, 5]
                `}
        </SyntaxHighlighter>
        <p>
           <span className={s.Span}>filter()</span>    
              callback-,    
            ,   ,   
          .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          //  
            const numbers = [1, 2, 3, 4, 5];
            const filteredNumbers = numbers.filter(value => value > 3);
            console.log(filteredNumbers); // [4, 5] 
                `}
        </SyntaxHighlighter>
      </div>
    </div>
  );
};

export default CallBack;
