            <li>
              <a href="#branching"></a>
            </li>
            <li>
              <a href="#ternary operator"> </a>
            </li>
            <li>
              <a href="#switch"> switch</a>
            </li>
            <li>
              <a href="#fieldOfView"> </a>
            </li>

            <li>
              <a href="#Cycles"></a>
            </li>
          </div>
        </Scrollspy>
      </div>
      <div className={s.article}>
        <h1 id="branching"></h1>
        <p>

          .    -     
          <span className={s.Span}>SyntaxHighlightertrue</span> {" "}
          <span className={s.Span}>SyntaxHighlighterfalse</span>  
                 .
        </p>
        <h4>
          {" "}
          <strong>
            <span className={s.Span}>SyntaxHighlighterif</span>
          </strong>
        </h4>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <img src={ifImg} alt="" />
        </div>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
                 if () {
                //  if
                }
                `}
        </SyntaxHighlighter>
        <p>
           ,    , {" "}
          <strong></strong> .    {" "}
          <span className={s.Span}>SyntaxHighlighterif</span>   .
             {" "}
          <span className={s.Span}>SyntaxHighlightertrue</span>,  
              {" "}
          <span className={s.Span}>SyntaxHighlighterif</span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
                let cost = 0;
                const subscription = "pro";

                if (subscription === "pro") {
                cost = 100;
                }

                console.log(cost); // 100
                `}
        </SyntaxHighlighter>
        <p>
             {" "}
          <span className={s.Span}>SyntaxHighlighterfalse</span>,   
            .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
            let cost = 0;
            const subscription = "free";

            if (subscription === "pro") {
            cost = 100;
            }

            console.log(cost); // 0
                `}
        </SyntaxHighlighter>
        <h4>
          {" "}
          <strong>
            <span className={s.Span}>SyntaxHighlighterif...else</span>
          </strong>
        </h4>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <img src={ifElse} alt="" />
        </div>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
            if () {
            //  if
            } else {
            //  else
            }
                `}
        </SyntaxHighlighter>
        <p>
            <span className={s.Span}>SyntaxHighlighterif</span>{" "}
          ,     
          <span className={s.Span}>SyntaxHighlighterfalse</span>, 

          <span className={s.Span}>SyntaxHighlighterelse</span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
            let cost;
            const subscription = "free";

            if (subscription === "pro") {
            cost = 100;
            } else {
            cost = 0;
            }

            console.log(cost); // 0
                `}
        </SyntaxHighlighter>
        <p>
             {" "}
          <span className={s.Span}>SyntaxHighlightertrue</span>,  
          <span className={s.Span}>SyntaxHighlighterelse</span> .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
            let cost;
            const subscription = "pro";

            if (subscription === "pro") {
            cost = 100;
            } else {
            cost = 0;
            }

            console.log(cost); // 100
                `}
        </SyntaxHighlighter>
        <h3>
