import React from "react";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { materialDark } from "react-syntax-highlighter/dist/esm/styles/prism";

import s from "./Lessons.module.css";
import sprite from "../../img/icomoon/sprite.svg";
import Scrollspy from "react-scrollspy";
import callBack01 from "../../img/callBack01.jpeg";
import declarative from "../../img/declarative.jpeg";
const This = () => {
  const codeStyle = materialDark;
  return (
    <div className={s.lessonsBox}>
      <div className={s.breadNav}>
        <Scrollspy
          items={["branching", "section-2", "section-3"]}
          currentClassName="is-current"
        >
          <div className={s.breadNavList}>
            <li>
              <a href="#context">  </a>
            </li>
            <li>
              <a href="#rool">  this</a>
            </li>
            <li>
              <a href="#fnMethod"> </a>
            </li>
          </div>
        </Scrollspy>
      </div>
      <div className={s.article}>
        <h1 id="context">  </h1>
        <p>
             ,   {" "}
          <span className={s.Span}>this</span>     
           JavaScript   .   {" "}
          <span className={s.Span}>this</span>    , 
            .
        </p>
        <p>  JavaScript     :</p>
        <ul>
          <li>
            <span className={s.Span}></span>  , {" "}
            <span className={s.Span}></span>   .
          </li>
          <li>
            <span className={s.Span}></span>  , {" "}
            <span className={s.Span}></span>   .
          </li>
        </ul>
        <p>
             .     
            ,      ,    
                 .   
            .
        </p>
        <p>
             '      .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          //    ,     .
          const petro = {
            username: "Petro",
            showName() {
              console.log(petro.username);
            },
          };

          petro.showName();
                `}
        </SyntaxHighlighter>
        <p>
             '  , 
          '  ',  {" "}
          <span className={s.Span}></span> {" "}
          <span className={s.Span}></span>.
        </p>
        <p>

          <span className={s.Span}>this</span>.    {" "}
          <span className={s.Span}>this</span>    ',
               .  ,    
                  '.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          //    ,   (this)   .
          const petro = {
            username: "Petro",
            showName() {
              console.log(this.username);
            },
          };

          petro.showName();
                `}
        </SyntaxHighlighter>
        <p>    :</p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const bookShelf = {
            authors: [" ", " "],
            getAuthors() {
              return this.authors;
            },
            addAuthor(authorName) {
              this.authors.push(authorName);
            },
          };

          console.log(bookShelf.getAuthors()); // [" ", " "]
          bookShelf.addAuthor(" ");
          console.log(bookShelf.getAuthors()); // [" ", " ", " "]                `}
        </SyntaxHighlighter>
        <p>
           <span className={s.Span}>getAuthors</span> {" "}
          <span className={s.Span}>addAuthor</span>  ( '), 
             ' <span className={s.Span}>bookShelf</span>.
              <span className={s.Span}>this</span> 
            ' <span className={s.Span}>bookShelf</span>  
                .
        </p>
        <h1 id="rool">
            <span className={s.Span}>this</span>
        </h1>
        <p>
               {" "}
          <span className={s.Span}>this</span> -   
           ( ) {" "}
          <strong>    </strong> , {" "}
          <strong> </strong>.  
          <span className={s.Span}>this</span>  ,  
          ,      .
        </p>
        <h3>
          <span className={s.Span}>this</span>    
        </h3>
        <p>
             ,     
          , <span className={s.Span}>this</span>   '{" "}
          <span className={s.Span}>window</span>.    {" "}
          <span className={s.Span}>this</span>,    ,
           <span className={s.Span}>undefined</span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function foo() {
            console.log(this);
          }

          foo(); // window  "use strict"  undefined  "use strict"
                `}
        </SyntaxHighlighter>
        <h3>
          <span className={s.Span}>this</span>   {" "}
        </h3>
        <p>
                ',   
           ',    .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const petro = {
            username: "Petro",
            showThis() {
              console.log(this);
            },
            showName() {
              console.log(this.username);
            },
          };

          petro.showThis(); // {username: "Petro", showThis: , showName: }
          petro.showName(); // 'Petro'
                `}
        </SyntaxHighlighter>
        <p>     .</p>
        <ul>
          <li>

             .
          </li>
          <li>
                  '    
             '.
          </li>
        </ul>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function showThis() {
            console.log("this in showThis: ", this);
          }

          //    
          showThis(); // this in showThis: Window

          const user = {
            username: "Mango",
          };

          //       
          //  ,     -  ()
          user.showContext = showThis;

          //     
          // this     ,  
          //   ,     .
          user.showContext(); // this in showThis: {username: "Mango", showContext: }
                `}
        </SyntaxHighlighter>
        <h3>
          <span className={s.Span}>this</span>  callback-
        </h3>
        <p>
             '  -  
          .     ,   
           ,   '.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const customer = {
            firstName: "Jacob",
            lastName: "Mercer",
            getFullName() {
              return \`$\{this.firstName} $\{this.lastName}\`;
            },
          };

          function makeMessage(callback) {
            // callback()    getFullName  
            console.log(\`   $\{callback()}.\`);
          }

          makeMessage(customer.getFullName); //      
                `}
        </SyntaxHighlighter>
        <div className={s.interestBox}>
          <div className={s.interestHeader}>
            <svg className={s.textIcon}>
              <use href={sprite + "#target"}></use>
            </svg>
            <h5></h5>
          </div>
          <p>
                  {" "}
            <strong> bind()   '</strong> .
          </p>
        </div>
        <h3>
          <span className={s.Span}>this</span>   
        </h3>
        <p>
               <span className={s.Span}>this</span>
          .     ,  {" "}
          <span className={s.Span}>this</span>   
            .
        </p>
        <div className={s.interestBox}>
          <div className={s.interestHeader}>
            <svg className={s.textIcon}>
              <use href={sprite + "#target"}></use>
            </svg>
            <h5></h5>
          </div>
          <p>
               {" "}
            <strong>   </strong>,     
               .
          </p>
        </div>
        <p>
                . 
           '  , {" "}
          <span className={s.Span}>this</span>     {" "}
          <span className={s.Span}>window</span>   , 
               .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const showThis = () => {
            console.log("this in showThis: ", this);
          };

          showThis(); // this in showThis: window

          const user = {
            username: "Mango",
          };
          user.showContext = showThis;

          user.showContext(); // this in showThis: window
                `}
        </SyntaxHighlighter>
        <p>
              , JavaScript-
             ,     ,
           <span className={s.Span}>this</span>    .
        </p>
        <p>
            ,   ,    
          .       .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const hotel = {
            username: "Resort hotel",
            showThis() {
              const foo = () => {
                //      ,
                //    
                console.log("this in foo: ", this);
              };

              foo();
              console.log("this in showThis: ", this);
            },
          };

          hotel.showThis();
          // this in foo: {username: 'Resort hotel', showThis: }
          // this in showThis: {username: 'Resort hotel',showThis: }
                `}
        </SyntaxHighlighter>
        <h1 id="fnMethod"> </h1>
        <p>
           ,       
          ',       .     

          <span className={s.Span}>call</span>,{" "}
          <span className={s.Span}>apply</span> {" "}
          <span className={s.Span}>bind</span>.
        </p>
        <h3>
           <span className={s.Span}>call()</span>
        </h3>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          foo.call(obj, arg1, arg2, ...)
                `}
        </SyntaxHighlighter>
        <p>
           <span className={s.Span}>call</span>  {" "}
          <span className={s.Span}>foo</span> ,  {" "}
          <span className={s.Span}>this</span>    '{" "}
          <span className={s.Span}>obj</span>,    {" "}
          <span className={s.Span}>arg1</span>,{" "}
          <span className={s.Span}>arg2</span>  ..
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function greetGuest(greeting) {
            console.log(\`$\{greeting}, $\{this.username}.\`);
          }

          const mango = {
            username: "",
          };
          const poly = {
            username: "",
          };

          greetGuest.call(mango, " "); //  , .
          greetGuest.call(poly, " "); //  , .
                `}
        </SyntaxHighlighter>
        <h3>
           <span className={s.Span}>apply</span>
        </h3>
        <p>
           <span className={s.Span}>apply</span>   {" "}
          <span className={s.Span}>call</span>  ,   
             ,  ,   
           .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          foo.call(obj, arg1, arg2, ...)

          foo.apply(obj, [arg1, arg2, ...])
                `}
        </SyntaxHighlighter>
        <p>
           <span className={s.Span}>apply</span>  {" "}
          <span className={s.Span}>foo</span> , 
          <span className={s.Span}>this</span>    '{" "}
          <span className={s.Span}>obj</span>,     
             <span className={s.Span}>arg1</span>,{" "}
          <span className={s.Span}>arg2</span>  ..
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function greetGuest(greeting) {
            console.log(\`$\{greeting}, $\{this.username}.\`);
          }

          const mango = {
            username: "",
          };
          const poly = {
            username: "",
          };

          greetGuest.apply(mango, [" "]); //  , .
          greetGuest.apply(poly, [" "]); //  , .
                `}
        </SyntaxHighlighter>
        <h3>
           <span className={s.Span}>bind()</span>
        </h3>
        <p>
           <span className={s.Span}>call</span> {" "}
          <span className={s.Span}>apply</span>    ,
           .    -,    
           ,     ,   '
          ,  {" "}
          <span className={s.Span}>bind</span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          foo.bind(obj, arg1, arg2, ...)
                `}
        </SyntaxHighlighter>
        <p>
           <span className={s.Span}>bind</span>    
           <span className={s.Span}>foo</span>  ' {" "}
          <span className={s.Span}>obj</span>  {" "}
          <span className={s.Span}>arg1</span>,{" "}
          <span className={s.Span}>arg2</span>  . .   ,
                  .
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          function greet(clientName) {
            return \`$\{clientName},    $\{this.service}.\`;
          }

          const steam = {
            service: "Steam",
          };
          const steamGreeter = greet.bind(steam);
          steamGreeter(""); // ",    Steam."

          const gmail = {
            service: "Gmail",
          };
          const gmailGreeter = greet.bind(gmail);
          gmailGreeter(""); // ",    Gmail."
                `}
        </SyntaxHighlighter>
        <h3>
          <span className={s.Span}>bind()</span>   {" "}
        </h3>
        <p>
             '  -  
          .     ,   
           ,   '.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          const customer = {
            firstName: "Jacob",
            lastName: "Mercer",
            getFullName() {
              return \`$\{this.firstName} $\{this.lastName}\`;
            },
          };

          function makeMessage(callback) {
            // callback()    getFullName  
            console.log(\`   $\{callback()}.\`);
          }

          makeMessage(customer.getFullName); //      
                `}
        </SyntaxHighlighter>
        <p>
            ,  <span className={s.Span}>this</span> 
           <span className={s.Span}>getFullName</span>,   
          - <span className={s.Span}>callback()</span>, {" "}
          <span className={s.Span}>undefined</span>.   
           <span className={s.Span}>firstName</span> {" "}
          <span className={s.Span}>lastName</span> , {" "}
          <span className={s.Span}>undefined</span>  '.
        </p>
        <p>
           <span className={s.Span}>bind</span>  
          '      ' 
          -.     {" "}
          <span className={s.Span}>getFullName</span>,    
          '   '{" "}
          <span className={s.Span}>customer</span>.
        </p>
        <SyntaxHighlighter language="javascript" style={codeStyle}>
          {`
          //  
          makeMessage(customer.getFullName); //      

          //  
          makeMessage(customer.getFullName.bind(customer)); //    Jacob Mercer.
                `}
        </SyntaxHighlighter>
      </div>
    </div>
  );
};

export default This;
